pragma solidity >=0.5.1 <0.7.0;
// pragma experimental ABIEncoderV2;

contract JurStatus {
    // Represents a single status.
    struct Status {
      uint timestamp; // timestamp from which the status will be valid
      bool active; // boolean state depicting if a status is currently active
      bytes32 statusType; // the type of the status
    }

    // This is a type for a single status-type.
    struct Type {
      bytes32 name; // short name (up to 32 bytes)
    }

    address owner;

    // This declares a state variable - status that
    // stores a `Status` struct for each possible address.
    mapping(address => Status) public status;

    // string[] public statusTypes;

    // A dynamically-sized array of `Type` structs.
    Type[] public statusTypes;


    /** Total count of Jur Statuses. */
    // uint public statusCount;

    // event Logger(string s, string[] u);
    event StatusTypeAdded(uint timestamp, bytes32 statusType, string notification);
    event StateChanged(uint timestamp, address statusHolder, bool newState, string notification);
    event StatusAdded(uint timestamp, address statusHolder, string statusType, string notification);

    modifier onlyOwner() {
      require(msg.sender == owner, "Access not granted.");
      _;
    }

    constructor()
    public {
      owner = msg.sender;
    }

    /**
      @dev addStatus - function to let the Jur admin add new address as a Jur Status, thus
      validating it's license which can be verified on the blockchain.
      @param _statusHolder - The address which will hold the Jur Status.
      @param _position - The position of the value from the statusTypes array signifying the
      status type.
    */
    function addStatus(
      address _statusHolder,
      uint _position
    ) public
    onlyOwner {
      require(_statusHolder != address(0), "Please provide a valid address.");
      // require(statusTypes.length == 0, "Empty.");
      // require(_position >= statusTypes.length, "Position does not exist.");
      status[_statusHolder] = Status(now, true, statusTypes[_position].name);
      // statusCount++;

      // emit StatusAdded(now, _statusHolder, statusTypes[_position], "Status added");
    }

    /**
      @dev changeState - function to let the Jur admin change the state of a status.
      @param _statusHolder - The address holding the Jur Status.
      @param _newState -  Boolean status to update.
    */
    function changeState(
      address _statusHolder,
      bool _newState
    ) public
    onlyOwner {
      require(_statusHolder != address(0), "Please provide a valid address.");
      // require(status[_statusHolder].timestamp != 0, "Address is not a Jur Status holder.");
      require(status[_statusHolder].active != _newState, "Already in the same state.");
      status[_statusHolder].active = _newState;

      emit StateChanged(now, _statusHolder, _newState, "Active state changed");
    }

    /**
      @dev addStatusType - function to let an admin add status types
      @param _statusType - The new status type.
    */
    function addStatusType(
      bytes32 _statusType
    ) public
    onlyOwner {
      // require(bytes(_statusType).length == 0, "Status type cannot be an empty string.");

      // `Type({...})` creates a temporary
      // Type object and `statusTypes.push(...)`
      // appends it to the end of `statusTypes`.
      statusTypes.push(Type(_statusType));

      emit StatusTypeAdded(now, _statusType, "Status type added");
    }
}

